/**
 * Interface NamedErrorType
 * @interface
 */
export interface NamedErrorType {
    /**
     * Propriedade para definir data de criação
     * @type {Date}
     */
    createdAt: Date;
    /**
     * Propriedade para definir identificador de correlação da requisição. Esse identificador é utilizado para recuperar nos logs as pilhas de erro relacionadas com a requisição.
     * @type {string}
     */
    correlationId: string;
    /**
     * Propriedade para definir mensagem do erro
     * @type {string}
     */
    message: string;
    /**
     * Propriedade para definir mensagem completa do erro
     * @type {string}
     */
    fullMessage: string;
}
