/**
 * Class DatatablePaginationParameters
 * @class
 */
export interface DatatablePaginationParameters {
    /**
     * Nome do índice no payload de retorno que contém a quantidade de registros totais da coleção.
     * Se for nulo, o componente irá considerar a quantidade de registros retornados como o total da coleção.
     * @type {string|null}
     */
    totalElementsIndex: string | null;
    /**
     * Nome do índice no payload de retorno que contém os registros. Se for nulo, o componente irá considerar
     * os dados disponíveis na raiz do retorno.
     * @type {string|null}
     */
    contentIndex: string | null;
}
